import React from 'react'
import styled from 'styled-components'
import sculture from '../images/s22.png'
import List from '../component/List'


function Hero() {
    return (
        <>
            <Heroo>
                <HeroContainer>
                    <div className="text">
                        <h1><span>S</span>cultures</h1>
                    </div>
                    <div className="image">
                        <img src={sculture} alt="" srcset="" />
                    </div>
                    <div class="triangle-left"></div>
                    <div class="triangle-overlay"></div>
                </HeroContainer>
            </Heroo>
            <List />
        </>
    )

}

export default Hero

const Heroo = styled.div`
min-height: 70vh;
width: 100%;

display: flex;
justify-content: center;
align-items: center;
`
const HeroContainer = styled.div`
width: 80%;
height: fit-content;

display: flex;


.text {
    width: 50%;
    display: flex;
justify-content: center;
align-items: center;
font-size: 40px;
    text-shadow: 2px 2px 2px black;

span {
    color: #DAA520;
    font-size: 100px;
}

}
.image {
    width: 40%;
    display: flex;
    justify-content: center;
    align-items: center;

    img {
        width: 70%;
        z-index: 1;
    }
}

.triangle-left {
	width: 0;
	height: 0;
	border-top: 300px solid transparent;
	border-right: 400px solid #DAA520;
	border-bottom: 300px solid transparent;
    position: absolute;
    top: 5%;
    right: 22%;
    
}
.triangle-overlay {
	width: 0;
	height: 0;
	border-top: 290px solid transparent;
	border-right: 390px solid #131315;
	border-bottom: 290px solid transparent;
    position: absolute;
    top: 7%;
    right: 22.5%;
    
}

`
